      const newArtistlist = artistsFull.filter((a) => {
        return Object.values(a)
          .join(" ")
          .toLowerCase()
          .includes(searchTerm.toLowerCase());
      });
      setArtistsResults(newArtistlist);
      console.log(artistsResults)
    } else {
      setArtistsResults(artistsFull);
      console.log(artistsFull)
    }
  };




 //retrieve all from db
  const retrieveAllArtists = async () => {
    const response = await apiArists.get("/artists");
    return response.data;
  };
 const retrieveLocations = async () => {
    const response = await apiArists.get("/locations");
    return response.data;
  };


//Set full artist results once as for all 
  useEffect(() => { 
    const getArtistsResults = async () => {
      const allArtists = await retrieveAllArtists();
      if (allArtists) setArtistsFull(allArtists);
    };
    const getLocationResults = async () => {
      const results = await retrieveLocations();
      if (results) setLocationsFull(results);
    };
    getArtistsResults();
    getLocationResults();
