import React, { useState,useEffect, useRef } from 'react';
import {axios} from "./axios";

import HeaderNavbar from "./components/headerNavbar";
import HomePage from "./pages/HomePage";

const App = () => {
    const [artistsFull, setArtistsFull] =useState([])
    const [locationsFull, setLocationsFull] =useState([])
    const [searchTerm, setSearchTerm] = useState("");
    const [artistsResults, setArtistsResults] = useState([]);
    const [locationsResults, setLocationsResults] = useState([]);
    const inputEl = useRef("");


//**** GET DATA  */
const getAllArtists = async()=>{
  const response = await axios.get("/artists").catch((err)=>console.log("Error: ",err))
  if(response && response.data) {
    setArtistsFull(response.data)
    setArtistsResults(response.data)
  }
}
const getAllLocations = async()=>{
  const response = await axios.get("/locations").catch((err)=>console.log("Error: ",err))
  if(response && response.data) setLocationsFull(response.data) 
}

useEffect(() => {
  getAllArtists();
  getAllLocations();
},[])

const getLocationsList =(artistslist)=>{
  const locationsIdList = artistslist.map(a=> a.location)
  const locationsToMark = locationsFull.filter(m => locationsIdList.includes(m.id))
  setLocationsResults(locationsToMark)

}



//return only artist with names that includes the input
  const searchHandler = () => {
    setSearchTerm(inputEl.current.value);

    if (searchTerm !== "") {
      const newArtistlist = artistsFull.filter((a) => {
        return Object.values(a)
          .join(" ")
          .toLowerCase()
          .includes(searchTerm.toLowerCase());
      });
      setArtistsResults(newArtistlist);
      //get distinct locations array from artistresults list
      getLocationsList(newArtistlist);

    } else {
      setArtistsResults(artistsFull);
      getLocationsList();
    }
  };






// 1. add search textbox, 2.get values from it , 3.filterartist list 4.get location from artist 5.setLocation 5.mark map

    return ( 
        <>
        <HeaderNavbar/>
        <div>
           <input
            ref={inputEl}
            type="text"
            className="prompt"
            onChange={searchHandler}
          />

            <HomePage artists={artistsResults} locations={locationsResults}/>       

        </div>
        </>
     );
}

export default App;
