import React, { useState } from 'react';
import { v4 as uuid } from 'uuid';
import { axios } from '../axios';
import '../styles/adminForms.css';
// To Do :
//       1. add form validation
//       2. populate genres/location  select field list from db.js
//       3. css
//       4. add sucsses mesages
//

const AddNewArtist = ({ closeAddNewFormCB }) => {
      const [form, setForm] = useState({
            name: '',
            type: '',
            birth_name: '',
            location: '',
      });

      const postArtist = async (request) => {
            const response = await axios.post('/artists', request);
            console.log('exit');
      };

      const handleChange = (e) => {
            setForm({
                  ...form,
                  [e.target.name]: e.target.value,
            });
      };

      const handleSubmit = (e) => {
            e.preventDefault();
            const request = {
                  id: uuid(),
                  ...form,
            };
            postArtist(request);

            setForm({ name: '', type: '', birth_name: '', location: '' });
            closeAddNewFormCB();
      };

      return (
            <div className='formContainer'>
                  <h1 className='brandFont'>new artist</h1>
                  <form className='formContainer'>
                        <fieldset className='row'>
                              <label>Name</label>
                              <input
                                    type='text'
                                    name='name'
                                    onChange={handleChange}
                                    placeholder='Enter Artits Name'
                              ></input>
                        </fieldset>
                        <fieldset className='row'>
                              <label>Location</label>
                              <input
                                    type='text'
                                    name='location'
                                    onChange={handleChange}
                                    placeholder='Enter Artits Location'
                              ></input>
                        </fieldset>
                        <fieldset className='row'>
                              <label>Genre</label>
                              <input
                                    type='text'
                                    name='genre'
                                    onChange={handleChange}
                                    placeholder='Enter Artits Genre'
                              ></input>
                        </fieldset>
                        <button
                              variant='primary'
                              type='submit'
                              onClick={handleSubmit}
                        >
                              Submit
                        </button>
                        <button
                              variant='worning'
                              type='close'
                              onClick={() => closeAddNewFormCB}
                        >
                              Cancel
                        </button>
                  </form>
            </div>
      );
};

export default AddNewArtist;
