import React, { useState } from 'react';
import { v4 as uuid } from 'uuid';
import { Form, Col, Button, Row } from 'react-bootstrap';
import { axios } from '../axios';

// To Do :
//       1. add form validation
//       2. populate genres/location  select field list from db.js
//       3. css
//

const AddNewArtist = ({ closeAddNewFormCB }) => {
      const [form, setForm] = useState({
            inputAristName: '',
            inputArtistType: '',
      });
      const [isSubmitted, setIsSubmitted] = useState(false);
      //   const [name, setName] = useState('');
      //   const [birthName, setBirthName] = useState('');
      //   const [genre, setGenre] = useState('');
      //   const [city, setCity] = useState('');
      //   const [type, setType] = useState('');

      const addArtistHandler = async (artist) => {
            console.log('entered handler');

            const request = {
                  id: uuid(),
            };

            const response = await axios.post('/artists', request);
            console.log('exit');
      };

      const handleChange = (e) => {
            setForm({
                  ...form,
                  [e.target.name]: e.target.value,
            });
            console.log(form);
      };

      const handleSubmit = (e) => {
            e.preventDefault();
            setIsSubmitted(true);
      };

      return (
            <Form>
                  {isSubmitted ? <div>Loading ... </div> : ''}

                  <Row>
                        <Form.Group as={Col} controlId='formGridName'>
                              <Form.Label>Name</Form.Label>
                              <Form.Control
                                    placeholder='Enter Artits Name'
                                    name='inputAristName'
                                    onChange={handleChange}
                              />
                        </Form.Group>

                        <Form.Group as={Col} controlId='formGridType'>
                              <Form.Label>Type</Form.Label>
                              <Form.Control
                                    as='select'
                                    defaultValue='Singer'
                                    name='inputArtistType'
                                    onChange={handleChange}
                              >
                                    <option>Singer</option>
                                    <option>Compositors</option>
                                    <option>Band</option>
                              </Form.Control>
                        </Form.Group>
                  </Row>

                  <Form.Group controlId='formGridBirthName'>
                        <Form.Label>Birth Name</Form.Label>
                        <Form.Control />
                  </Form.Group>

                  <Form.Group as={Col} controlId='formGridGenre'>
                        <Form.Label>Genre</Form.Label>
                        <Form.Control />
                  </Form.Group>

                  <Row>
                        <Form.Group as={Col} controlId='formGridCity'>
                              <Form.Label>City</Form.Label>
                              <Form.Control />
                        </Form.Group>

                        <Form.Group as={Col} controlId='formGridState'>
                              <Form.Label>State</Form.Label>
                              <Form.Control
                                    as='select'
                                    defaultValue='Choose...'
                              >
                                    <option>Choose...</option>
                                    <option>...</option>
                              </Form.Control>
                        </Form.Group>
                  </Row>

                  <Button variant='primary' type='submit'>
                        Submit
                  </Button>
                  <Button
                        variant='worning'
                        type='close'
                        onClick={() => closeAddNewFormCB}
                  >
                        Cancel
                  </Button>
            </Form>
      );
};

export default AddNewArtist;
