//child components
import HeaderNavbar from '../components/headerNavbar';
import Map from '../components/map';
import ArtistsSideBar from '../components/artistsSideBar';
import { useRef } from 'react';

//design
import 'bootstrap/dist/css/bootstrap.min.css';

const HomePage = (props) => {
      const { artists, locations, selectedArtistsCB, getInput } = props;
      const inputEl = useRef('');
      const [input, setInput] = useState();
      const selectArtist = ({ a }) => {
            console.log('select artists from homepage');
            selectedArtistsCB({ a });
      };
      const headerText = 'Admin';

      const sendSearchApp = () => {
            setInput(inputEl.target.value);
            getInput(input);
      };

      //homepage renderding
      return (
            <div>
                  <header>
                        <HeaderNavbar
                              handleText={headerText}
                              goToLink={'/#/login'}
                        />

                        <div className='searchrow'>
                              <label className='input'> Search Artist</label>
                              <input
                                    ref={inputEl}
                                    type='text'
                                    onChange={sendSearchApp}
                              />
                        </div>
                  </header>

                  <div className='container'>
                        {/* {console.log(genres)} */}

                        <div>
                              <Map
                                    locations={locations}
                                    selectedArtistsCB={selectArtist}
                              />
                        </div>
                        <div>
                              <ArtistsSideBar
                                    artists={artists}
                                    selectedArtistsCB={selectArtist}
                              />
                        </div>
                  </div>
            </div>
      );
};

export default HomePage;
