//child components
import HeaderNavbar from '../components/headerNavbar';
import Map from '../components/map';
import ArtistsSideBar from '../components/artistsSideBar';
import { useRef } from 'react';

//design
import 'bootstrap/dist/css/bootstrap.min.css';

const HomePage = (props) => {
      const { artists, locations, selectedArtistsCB, searchHandler } = props;
      const inputEl = useRef('');
      const selectArtist = ({ a }) => {
            console.log('select artists from homepage');
            selectedArtistsCB({ a });
      };
      const headerText = 'Admin';

      const goLoginPage = () => {
            console.log('link to login');
      };

      //homepage renderding
      return (
            <div>
                  <header>
                        <HeaderNavbar
                              handleText={headerText}
                              handleAction={'/#/login'}
                        />

                        <div className='searchrow'>
                              <label className='input'> Search Artist</label>
                              <input
                                    ref={inputEl}
                                    type='text'
                                    onChange={searchHandler}
                              />
                        </div>
                  </header>

                  <div className='container'>
                        {/* {console.log(genres)} */}

                        <div>
                              <Map
                                    locations={locations}
                                    selectedArtistsCB={selectArtist}
                              />
                        </div>
                        <div>
                              <ArtistsSideBar
                                    artists={artists}
                                    selectedArtistsCB={selectArtist}
                              />
                        </div>
                  </div>
            </div>
      );
};

export default HomePage;
